setTimeout( function(){
    var userActionItemId = $UserActionItemId.GCRAACT_SURROGATE_ID;
    var isCheckbox =  $ActionItemContentDetail.STATUS_COUNT === 1? true:false;
    var selectedResponse = null;
    if (isCheckbox) {
        var radio = document.getElementById("pbid-ActionItemStatusAgree-radio-0-0");
        radio.type = "checkbox";
   }
    var isResponseReviewRequired = false;
   if (typeof $ReviewStatus !==  'undefined' ){
       isResponseReviewRequired = true;
        $("input[id^='pbid-ActionItemStatusAgree-radio']").prop({disabled: isResponseReviewRequired });
   } 
      $("#pbid-ActionItemContentDetail-save-button").click(function(){
            selectedResponse = $("input[id^='pbid-ActionItemStatusAgree-radio']:checked")[0].id;
            var responseElementId = selectedResponse.split("pbid-ActionItemStatusAgree-radio-0-");
            isResponseReviewRequired = data[parseInt(responseElementId[1])].STATUS_REV_REQ_IND === 'Y'? true:false;
            if(isResponseReviewRequired){
                $("input[id^='pbid-ActionItemStatusAgree-radio']").prop({disabled: isResponseReviewRequired });
                $("#pbid-ActionItemContentDetail-save-button").prop({disabled: isResponseReviewRequired });
                window.params.isResponseLocked = true;
            }
       });

    for(var elementId = 0; elementId < data.length; elementId++){
        var responseElementId = "#pbid-ActionItemStatusAgree-label-0-"+elementId;
        var paperClipId = "pbid-ActionItemStatusAgree-paper-clip-0-"+elementId;
        var responseElement = $('#pbid-ActionItemStatusAgree-0').find(responseElementId);
        var allowedAttachments = data[elementId].STATUS_ALLOWED_ATTACHMENT;
        var responseId = data[elementId].STATUS_RULE_ID;
        if(allowedAttachments  > 0){    
                    AIP.ListItemPageCtrl.prototype.documentUploader (userActionItemId, paperClipId, responseElement, allowedAttachments , responseId, isResponseReviewRequired);
        }        
    }
    selectedResponse = $("input[id^='pbid-ActionItemStatusAgree-radio']:checked");
    if(selectedResponse.length>0){
        var selResElementId = selectedResponse[0].id;
        var index = selResElementId.split("pbid-ActionItemStatusAgree-radio-0-")[1];
        if($("#pbid-ActionItemStatusAgree-paper-clip-0-"+index).length >0){
            $("#pbid-ActionItemStatusAgree-paper-clip-0-"+index)[0].setAttribute("src", "../images/attach_icon_default.svg");
        }        
    }

    if (isCheckbox) {
        $('input[type="checkbox"]').click( function(){
		if($("input[type='checkbox']:checked").length ===1){
			$("#pbid-ActionItemContentDetail-save-button").removeAttr("disabled");
			if( $("#pbid-ActionItemStatusAgree-paper-clip-0-0").length >0){       
                $("#pbid-ActionItemStatusAgree-paper-clip-0-0")[0].setAttribute("src", "../images/attach_icon_default.svg");
            } else {                
                $("#pbid-ActionItemStatusAgree-paper-clip-0-0")[0].setAttribute("src", "../images/attach_icon_disabled.svg");
            }
        }else{
			$("#pbid-ActionItemContentDetail-save-button").attr("disabled", "disabled");
		}
    });
    } else{
    $('input[type="radio"]').click( function(){
        $('#pbid-ActionItemStatusAgree-0').find('input[type="image"]').attr("src","../images/attach_icon_disabled.svg");
        var id = $("input[type='radio']:checked")[0].id;
        id= id.split("pbid-ActionItemStatusAgree-radio-0-");
        if($("#pbid-ActionItemStatusAgree-paper-clip-0-"+id[1]).length >0){
                $("#pbid-ActionItemStatusAgree-paper-clip-0-"+id[1])[0].setAttribute("src", "../images/attach_icon_default.svg");
        }
    });
    }
},300);

